import React from "react";


const MovieCard = ({ movie: { imdbID, Year, Poster, Title, Type } },{setSelectedMovie } ) => {
  // imdbID => id
  // Year => année de sortie
  // Poster => image
  // Title => titre
  // Type => film, animé, ...

  return (
    <div className="movie" key={imdbID}>
      <div>
        <p>{Year}</p>
      </div>

      <div>
        {/* si l'API renvoie aucun poster ("N/A") alors on utilise un placeholder 400x400 */}
        <img
          src={Poster !== "N/A" ? Poster : "https://via.placeholder.com/400"}
          alt={Title}
          onClick={() => setSelectedMovie(imdbID)} // event listener sur le poster 
          
        />
      </div>

      <div>
        <span>{Type}</span>
        <h3>{Title}</h3>
      </div>
    </div>
  );
};

export default MovieCard;
