import React from "react";

const MovieCard = ({
  movie: { imdbID, Year, Poster, Title, Type },
  setSelectedMovie,
  isSelected,
}) => {
  // imdbID => id du film
  // Year => année de sortie
  // Poster => image
  // Title => titre
  // Type => drame, SF, comedie...

  return (
    <div className={`movie ${isSelected ? "selected" : ""}`} key={imdbID}>
      <div>
        <p>{Year}</p>
      </div>

      <div>
        {/* si l'API renvoie aucun poster ("N/A") alors on utilise un placeholder 400x400 */}
        <img
          src={Poster !== "N/A" ? Poster : "https://via.placeholder.com/400"}
          alt={Title}
          onClick={() => {
            setSelectedMovie(imdbID);
            console.log(`Film sélectionné: ${Title}`); // Affiche l'ID du film sélectionné dans la console
          }}
          className={isSelected ? "enlarged" : ""}
        />
      </div>

      <div>
        <span>{Type}</span>
        <h3>{Title}</h3>
      </div>
    </div>
  );
};

export default MovieCard;
