import React from "react";
import { API_URL } from "../config/constants";

const MovieCard = ({ movie: { imdbID, Year, Poster, Title, Type } }) => {
  //id du film, année de sortie, image, titre, type(film/serie...)

  const handleMovieClick = (id) => {
    const getMovieDetails = async () => {
      try {
        const response = await fetch(`${API_URL}/movieDetails?id=${id}`);
        if (!response.ok) {
          throw new Error(
            `OMDb API request failed with status ${response.status}`
          );
        }
        const data = await response.json();
        console.log(data);
      } catch (error) {
        console.error("Une erreur est survenue:", error);
      }
    };

    getMovieDetails();
  };

  return (
    <div
      className="movie"
      key={imdbID}
      onClick={() => handleMovieClick(imdbID)}
    >
      <div>
        <p>{Year}</p>
      </div>

      <div className="img-container">
        {/* si l'API renvoie aucun poster ("N/A") alors on utilise un placeholder 400x400 */}
        <img
          src={Poster === "N/A" ? "https://via.placeholder.com/400" : Poster}
          alt={Title}
        />
      </div>

      <div className="bottom-card">
        <h2>{Type}</h2>
        <h3>{Title}</h3>
      </div>
    </div>
  );
};

export default MovieCard;
