import React from 'react';
import './index.css';
import './App.css';
import { useState } from "react";

function App() {
    // state (état, données)
    const [count, setCount] = useState(0) 

    // comportements (fonction fléchées)
    const handleClick = () => {
        setCount(count + 1)
    }   
        
    const handleClickM = () => {
        setCount(count - 1)
    };

    // affichage (render)
    return (
        <div className="container">
            <h1> {count} </h1>
            <button onClick={handleClick}>increase</button>
            <button onClick={handleClick}>decrease</button>
           
        </div>
    )


}



export default App;
