import React, { useState } from "react";
import MovieCard from "./components/MovieCard";
// Assurez-vous que le chemin vers SearchIcon est correct
import SearchIcon from "./search.svg";
import "./App.css";

const API_URL = "http://www.omdbapi.com?apikey=b6003d8a";

const App = () => {
    const [searchTerm, setSearchTerm] = useState("");
    const [movies, setMovies] = useState([]);
    const [message, setMessage] = useState("");

    // Assurez-vous que cette fonction est correctement encadrée
    const searchMovies = async (title) => {
        const response = await fetch(`${API_URL}&s=${title}`);
        const data = await response.json();
        setMovies(data.Search);

        if (data.Search && data.Search.length > 0) {
            setMovies(data.Search);
            setMessage(""); // Réinitialisez le message si des films sont trouvés
        } else {
            setMessage("Désolé, nous n'avons rien trouvé avec ce nom. Veuillez réessayer!");
        }

    };



    // Assurez-vous que le return est à l'intérieur de la portée du composant
    return (
        <div className="app">
            <h1> Movie finder </h1>

            <div className="search">
                <input
                    id="search-input"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    placeholder="Entrez un nom de film/série"
                />
                <img
                    src={SearchIcon}
                    alt="search"
                    onClick={() => searchMovies(searchTerm)}
                />
            </div>

            {movies?.length > 0 ? (
                <div className="container">
                    {movies.map((movie) => (
                        <MovieCard
                            key={movie.imdbID}
                            movie={movie}
                        />
                    ))}
                </div>
            ) : (
                <div className="empty">
                    <h2>{message}</h2>
                </div>
            )}
        </div>
    );
};

export default App;
