import React from "react";
import { useState } from "react";

function Count() {
  // state (état, données)
  const [count, setCount] = useState(3);

  // comportements (fonctions fléchées)
  const handleClickInc = () => {
    setCount(count + 1);
  };

  // affichage (render)
  return (
    <div>
      <div className="wrap-button">
        <button className="buttonLike" onClick={handleClickInc}>{count}❤️</button>
      </div>
    </div>
  );

}

export default Count;
