import React from "react";
// le component "Card" à un paramètre "user" 
// qui est un objet avec les propriétes "name,age et job"
const Card = ({ user }) => {
    const { name=stri, age, job=null } = user;

    return (
      <div className="card">
        {/* propriété que l'on veut attribuer
            au composant Card */}
        <p> {name} </p>
        <p> {`Age: ${age}`} </p>
        <p> {job} </p>
      </div>
    );
}

export default Card;
