import React from "react";
import { useState } from "react";



 function Count() {
  // state (état, données)
  const [count, setCount] = useState(0);

  // comportements (fonctions fléchées)
  const handleClickInc = () => {
    setCount(count + 1);
  };

  const handleClickDec = () => {
    setCount(count - 1);
  };

  // affichage (render)
  return (
    <div className="container">
      <h1> {count} </h1>
      <div className="wrap-button">
        <button onClick={handleClickDec}>&#x25BC;</button>
        <button onClick={handleClickInc}>&#11205;</button>
      </div>
    </div>
  );
}


export default Count;
