import React, { useState } from "react";
import API_URL from "../config/constants";

const MovieCard = ({ movie: { imdbID, Year, Poster, Title, Type } }) => {
  //id du film, année de sortie, image, titre, type(film/serie...)

  const [isMovieSelected, setIsMovieSelected] = useState(false);
  const [movieDetails, setMovieDetails] = useState(null);

  /**
   * Handle movie click event to fetch movie details from OMDb API
   * @async
   * @function
   * @param {string} id - Movie ID
   * @throws {Error} if API request fails
   */
  const handleMovieClick = async (id) => {
    setIsMovieSelected(true); // set to true when clicked

    try {
      const response = await fetch(`${API_URL}/movieDetails?id=${id}`);
      if (!response.ok) {
        throw new Error(
          `OMDb API request failed with status ${response.status}`
        );
      }
      const data = await response.json();
      setMovieDetails(data);
    } catch (error) {
      console.error("Une erreur est survenue:", error);
    }
  };

  // useEffect(() => {
  //   if (!isMovieSelected) {
  //     setMovieDetails(null);
  //     setError(null);
  //   }
  // }, [isMovieSelected]);

  return (
    <>
      {/* Affiche les films de la recherche par le titre */}
      {!isMovieSelected && (
        <div
          className="movie"
          key={imdbID}
          onClick={() => {
            handleMovieClick(imdbID); // passe l'id du film cliqué à handleMovieClick
          }}
        >
          <div>
            <p>{Year}</p>
          </div>

          <div className="img-container">
            {/* si l'API renvoie aucun poster ("N/A") alors on utilise un placeholder 400x400 */}
            <img
              src={
                Poster === "N/A" ? "https://via.placeholder.com/400" : Poster
              }
              alt={Title}
            />
          </div>

          <div className="bottom-card">
            <h2>{Type}</h2>
            <h3>{Title}</h3>
          </div>
        </div>
      )}
      {/* Affiche les details du film lorsqu'on clique sur celui-ci */}
      {isMovieSelected && movieDetails && (
        <div className="movie-details">
          <button onClick={() => setIsMovieSelected(false)}>Retour</button>
          <h2>{movieDetails.Title}</h2>
          <img
            src={
              movieDetails.Poster === "N/A"
                ? "https://via.placeholder.com/400"
                : movieDetails.Poster
            }
            alt={movieDetails.Title}
          />
          <p>
            <strong>Plot:</strong> {movieDetails.Plot}
          </p>
          <p>
            <strong>Genre:</strong> {movieDetails.Genre}
          </p>
          <p>
            <strong>Actors:</strong> {movieDetails.Actors}
          </p>
          <p>
            <strong>Director:</strong> {movieDetails.Director}
          </p>
          <p>
            <strong>Writer:</strong> {movieDetails.Writer}
          </p>
          <p>
            <strong>Awards:</strong> {movieDetails.Awards}
          </p>
          <p>
            <strong>Country:</strong> {movieDetails.Country}
          </p>
          <p>
            <strong>Language:</strong> {movieDetails.Language}
          </p>
          <p>
            <strong>Released:</strong> {movieDetails.Released}
          </p>
          <p>
            <strong>Runtime:</strong> {movieDetails.Runtime}
          </p>
          <p>
            <strong>Rated:</strong> {movieDetails.Rated}
          </p>
          <p>
            <strong>Metascore:</strong> {movieDetails.Metascore}
          </p>
          <p>
            <strong>imdbRating:</strong> {movieDetails.imdbRating}
          </p>
          <p>
            <strong>imdbVotes:</strong> {movieDetails.imdbVotes}
          </p>
          {movieDetails.totalSeasons && (
            <p>
              <strong>Total Seasons:</strong> {movieDetails.totalSeasons}
            </p>
          )}
          {movieDetails.Ratings && movieDetails.Ratings.length > 0 && (
            <div>
              <strong>Ratings : </strong>
              <ul>
                {movieDetails.Ratings.map((rating, index) => (
                  <li key={index}>
                    {rating.Source}: {rating.Value}
                  </li>
                ))}
              </ul>
            </div>
          )}
        </div>
      )}
      {isMovieSelected && (
        <button onClick={() => setIsMovieSelected(false)}>Retour</button>
      )}
    </>
  );
};

export default MovieCard;
