import React, { useState } from "react";
import MovieCard from "./components/MovieCard";
import SearchIcon from "./search.svg";
import "./App.css";

const API_URL = "http://www.omdbapi.com?apikey=b6003d8a";

const App = () => {
    // variable d'état qui contient la valeur de l'input de recherche
    const [searchTerm, setSearchTerm] = useState("");
    // variable d'état qui contient tous les films trouvés
    const [movies, setMovies] = useState([]);
    // variable d'état qui set un message en cas de film non trouvé
    const [message, setMessage] = useState("");


    const handleTerm = (e) => {
        // Permet l'effacement de la première lettre
        if (e.target.value === "") {
            setSearchTerm("");
        }
        // Regex qui refuse les caractères spéciaux dans l'input de recherche
        if (/^[a-zA-Z0-9\s]+$/.test(e.target.value)) {
            setSearchTerm(e.target.value);
            setMessage(""); //reset message "caractères spéciaux" si un caractére est valide
        } else {
            setMessage("Veuillez ne pas utiliser de caractères spéciaux");
        }
    };
    // soumission du form
    const handleSubmit = (event) => {
        event.preventDefault(); // Empêche le comportement par défaut du formulaire
        searchMovies(searchTerm); // Exécute la recherche
    }

    // fetch vers API avec gestion des erreurs
    const searchMovies = async (title) => {

        try {
            const response = await fetch(`${API_URL}&s=${title}`);

            if (!response.ok) throw new Error(response.status);

            const data = await response.json();
            setMovies(data.Search);

            if (data.Search && data.Search.length > 0) {
                setMessage(""); // reset le message si des films sont trouvés
            } else {
                setMessage(
                    "Désolé, nous n'avons rien trouvé avec ce nom. Veuillez réessayer!"
                );
            }
        } catch (error) {
            console.error("une erreur est survenue:", error);
            setMessage("Une erreur est survenue. Veuillez réessayer plus tard.");
        }
    };

    return (
        <div className="app">
            <h1> Movie finder </h1>
            <form onSubmit={(e) => handleSubmit(e)}>
                <div className="search">
                    <input
                        id="search-input"
                        value={searchTerm}
                        onChange={handleTerm}
                        placeholder="Entrez un nom de film/série"
                    />
                    <button type="submit">
                        <img
                            src={SearchIcon}
                            alt="Recherche"
                        />

                    </button>
                </div>
            </form>

            {movies?.length > 0 ? (
                <div className="container">
                    {movies.map((movie) => (
                        <MovieCard key={movie.imdbID} movie={movie} />
                    ))}
                </div>
            ) : (
                <div className="empty">
                    <h2>{message}</h2>
                </div>
            )}
        </div>
    );
};

export default App;
