import React from "react";
// le component "Card" à un paramètre "user" 
// qui est un objet avec les propriétes "name,age et job"
const Card = ({ user }) => {
    const { name=String, age=Number, job=null } = user;

  return (
      <>
        <div className="card">
        {/* propriété que l'on veut attribuer
            au composant Card */}
        <p className="cardName"> {name} </p>
        <p> {`Age : ${age}`} </p>
        <p> {`Job : ${job}`}</p>
      </div>
      <div>
          
      </div>
      ></>
    );
}

export default Card;
