import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import './App.css'
import { useState } from "react";

function App() {
    // state (état, données)
    const [count, setCount] = useState(0) 

    // comportements (fonction fléchées)
    const handleClick = () => {
        setCount(count +1)
    };
    // affichage (render)
    return (
        <>
            <h1> {count} </h1>
            <button onClick={handleClick}>increment</button>
        </>
    )


}


    


export default App;
